PartialViews.View:
@using Umbraco.Community.BlockPreview.Extensions
@using Project1.Controllers
@using Our.Umbraco.TagHelpers
@inherits Umbraco.Cms.Web.Common.Views.UmbracoViewPage<Umbraco.Cms.Core.Models.Blocks.BlockGridItem>

@{
    var  dataAreas = "";
    foreach (var area in Model.Areas)
    {
        dataAreas += area.Alias + " ";
    }
    var formSettings = (dynamic)Model.Settings;
}
<!-- START - AREAS -->
<div class="areas grid container" data-areas="@dataAreas" data-ContentUdi="@Model.ContentUdi" data-Content="@Model.Content" data-GridColumns="@Model.GridColumns">
    <!-- Form wrapper -->
    @using (Html.BeginUmbracoForm<FormsSurfaceController>((string)formSettings.FormAction, null, new { @class = "g-col-12 needs-validation", action = formSettings.FormAction }, FormMethod.Post))
    {
        <legend our-if="@formSettings != null && @formSettings.FormDisplayLegend">@formSettings.FormTitle</legend>
        @foreach (var area in Model.Areas)
        {
            @await Html.GetPreviewBlockGridItemAreaHtmlAsync(area)
        }
    }
    <!-- end Form wrapper -->
</div>
<!-- END - AREAS -->
